import React from 'react'
const imgTextInfor=[
  {
    title:"이미지 텍스트 영역",
    text1:"이미지/텍스트 유형",
    text2:"유용한 사이트",
    text3:"살펴보기",
    desc1:"사이트 제작에 있어 이미지와 텍스트를 혼합해서 사용하는 경우가 많다.",
    desc2:"기본 레이아웃은 동일하기 때문에 이미지나 배치에 따라서 느낌은 얼마든지 다르게 표현할 수 있다.",
    desc3:"Gmarket Sans 50px, Medium",
    desc4:"NEXON Lv1 Gothic 18px (153%)",
    text4:"더 많은 유형 살펴보기",
    id:1
  }
]
function Info({title,text1,text2,text3,text4,desc1,desc2,desc3,desc4}){
    return(
        <>
            <h2 className="ir_so">{title}</h2>
            <div className="imgText__inner container">
                <div className="txt">
                    <span className="small">{text1}</span>
                    <h3 className="title">
                    {text2}
                    <br/>
                    {text3}
                    </h3>
                    <p className="desc">
                    {desc1}
                    <br/>
                    {desc2}
                    <br/>
                    {desc3}
                    <br/>
                    {desc4}
                    </p>
                    <a href="/" className="btn">
                    {text4}
                    </a>
                </div>
            </div>
        </>
    )
}
function ImgTextCont() {
  return (
    <section id="imgTextType" className="imgText__wrap section nexon gray">
        {imgTextInfor.map((txt)=>(
            <Info 
            title={txt.title} 
            text1={txt.text1} 
            text2={txt.text2} 
            text3={txt.text3} 
            text4={txt.text4} 
            desc1={txt.desc1} 
            desc2={txt.desc2} 
            desc3={txt.desc3} 
            desc4={txt.desc4} 
            key={txt.id}
            />
        ))}

        <div className="img bg1"></div>
        <div className="img bg2"></div>
     </section>
  )
}

export default ImgTextCont
