import React from 'react'
const imageText=[
    {
        image:"img/image_bg01.jpg",
        title:"애교만점 웰시코기",
        desc1:"제목은 넥슨 풋볼 고딕 폰트입니다.",
        desc2:"설명부분은 넥슨 LV1 고딕 16px 140% 입니다.",
        text:"자세히 보기",
    },
    {
        image:"img/image_bg02.jpg",
        title:"코카스 파니엘",
        desc1:"제목은 넥슨 풋볼 고딕 폰트입니다..",
        desc2:"설명부분은 넥슨 LV1 고딕 16px 140% 입니다.",
        text:"자세히 보기",
        class:"image__btn yellow",
        btn:""
    }
]
function Info({image,title,desc1,desc2,text,color,btn}){
    return(
        <>
            <article className="image">
                <div className="image__header">
                    <figure className="image__figure">
                        <img src={image} alt={title} />
                    </figure>
                </div>
                <div className="image__body">
                    <h3 className="image__title">{title}</h3>
                    <p className="image__desc">
                        {desc1}
                        <br/>
                        {desc2}
                    </p>
                    <a href="/" className={btn,color}>
                        {text}
                    </a>
                </div>
            </article>
        </>
    )
}
function ImageCont() {
  return (
    <section id="imageType"  className="card__wrap section nexon">
        <h2>포트폴리오가 실력이다.</h2>
        <p>
        이미지 유형입니다. 마우스 오버시 자세한 정보가 나와는 구조입니다.
        </p>
        <div className="image__inner container">

            {imageText.map((txt)=>(
                <Info 
                image={txt.image} 
                title={txt.title} 
                desc1={txt.desc1} 
                desc2={txt.desc2} 
                text={txt.text} 
                color={txt.color}
                key={txt.text}
                />
            ))}
        </div>
    </section>
  )
}

export default ImageCont
