import React from 'react'
const BannerInfor=[
    {
        title:"배너 영역",
        text1:"유튜버 웹보이",
        desc:"더 다양한 강의는 유튜브를 통해 제공하고 있습니다.",
        address:"youtube.com/c/Webstoryboy",

    }
]

function Info({title,text1,desc,address}){
    return(
        <>
            <h2 className="ir_so">{title}</h2>
            <div className="banner__inner">
                <h3 className="banner__title">{text1}</h3>
                <p className="banner__desc">
                    {desc}
                    <a href="/">{address}</a>
                </p>
                <span className="banner__sub">배너 영역01</span>
            </div>
        </>
    )
}
function BannerCont() {
  return (
    <section id="bannerType" className="banner__wrap section gmarket">
        {imageInfor.map((txt)=>(
                <Info 
                image={txt.image} 
                title={txt.title} 
                desc1={txt.desc1} 
                desc2={txt.desc2} 
                text={txt.text} 
                key={txt.text}
                />
            ))}
    </section>
  )
}

export default BannerCont
