import React from 'react'


function BannerInfo({title,desc,address}){
    return(
        <div>
            <h2 className="ir_so">{title}</h2>
            <div className="banner__inner">
                <h3 className="banner__title">{title}</h3>
                <p className="banner__desc">
                    {desc}
                    <a href="/">{address}</a>
                </p>
                <span className="banner__sub">{title}</span>
            </div>
        </div>
    )
}
function BannerCont() {
  return (
    <section id="bannerType" className="banner__wrap section gmarket">
        {bannerText.map((txt)=>(
            <BannerInfo
            key={txt.title}
            title ={txt.title}
            desc={txt.desc}
            address={txt.address}
            />
        ))}
    </section>
  )
}

export default BannerCont
