import React from 'react'
const bannerText = [
  {
    title: "배너 영역",
   },
  {
   title: "유튜버 웹보이",
   desc:"더 다양한 강의는 유튜브를 통해 제공하고 있습니다." ,
  },
  {
    title:"배너 영역01",
    address:"youtube.com/c/Webstoryboy"
  }
]

function BannerInfo({title,desc,address}){
    return(
        <div>

        <h2 className="ir_so">{title}</h2>
          <div className="banner__inner">
            <h3 className="banner__title">{title}</h3>
              <p className="banner__desc">
                {desc}
                <a href="/">{address}</a>
              </p>
            <span className="banner__sub">{title}</span>
        </div>
        </div>
    )
}
function BannerCont() {
  return (
    <section id="bannerType" className="banner__wrap section gmarket">
        {bannerText.map((txt)=>(
            <BannerInfo
            key={txt.title}
            title ={txt.tile}
            desc={txt.desc}
            address={txt.address}
            />
        ))}
    </section>
  )
}

export default BannerCont
