import { func } from 'prop-types'
import React from 'react'

const sldierInfor4 =[
    {text:"이전이미지"},
    {text:"다음이미지"},
    {text:"1"},
    {text: "2"},
    {text: "3"},
    {text: "4"},
    {text: "자동플레이"},
    {text: "정지"},
]
function Info5({text}){
    return(
        <>
            <a href="/" className="dot"> 
                <span className="ir_pm">{text}</span>
            </a>
        </>
    )
}
const sliderInfor1=[
    {
        title:"DEVELOPER",
        text1:"NEW FRONTEND",
        desc1:"당신이 만들어갈 새로운 이야기",
        desc2:"당신도 할 수 있습니다.",
        text2:"자세히 보기",
        text3:"사이트 보기"
    }
]
function Info1({title,text1,desc1,desc2,text2,text3}){
    return(
        <>
            <div className="desc">
                <span>{title}</span>
                <h3>{text1}</h3>
                <p>
                {desc1}
                <br /> {desc2}
                </p>
                <div className="btn">
                    <a href="/" className="white">
                        {text2}
                    </a>
                    <a href="/" className="black">
                        {text3}
                    </a>
                </div>
            </div>
        </>
    )
}
const sliderInfor2=[
    {
        text:"이전이미지"
    }
]
function Info2({text}){
    return(
        <>
            <a href="/" className="left">
                <span className="ir_pm">{text}</span>
            </a>
        </>
    )
}
const sliderInfor3=[
    {
        text:"다음이미지"
    }
]
function Info3({text}){
    return(
        <>
            <a href="/" className="right">
                <span className="ir_pm">{text}</span>
            </a>
        </>
    )
}
function SliderCont(props) {
  return (
<section id="sliderType" className={`${props.fonts[0]} ${props.fonts[1]}`}>
    <h2 className="ir_so">슬라이드 영역</h2>
    <div className="slider__inner">
        <div className="slider">
            <div className="slider__img">
                {sliderInfor1.map((txt)=>(
                    <Info1 
                    title={txt.title} 
                    text1={txt.text1} 
                    text2={txt.text2} 
                    text3={txt.text3} 
                    desc1={txt.desc1} 
                    desc2={txt.desc2}  
                    key={txt.title}
                    />
                ))}
            </div>
            <div className="slider__arrow">
                {sliderInfor2.map((txt)=>(
                    <Info2 text={txt.text} key={txt.text}/>
                ))}
                {sliderInfor3.map((txt)=>(
                    <Info3 text={txt.text} key={txt.text}/>
                ))}

            </div>
            <div className="slider__dot">
                <a href="/" className="dot active"> </a>

                <a href="/" className="dot"> </a>
                <a href="/" className="dot"> </a>
                <a href="/" className="play"> </a>
                <a href="/" className="stop"> </a>
                {sldierInfor5.map((txt)=>(
                    <Info5 text={txt.text} key={txt.text}/>
                ))}
            </div>
        </div>
    </div>
</section>

  )
}

export default SliderCont
